#%% Resume Project 1import pandas as pdimport numpy as npimport yfinance as yffrom yahoofinancials import YahooFinancialsimport datetimefrom dateutil.relativedelta import relativedelta#%% Import Price Datanow = datetime.datetime.now()start = now + relativedelta(years=-2) + relativedelta(days=1)data = yf.download('UBS',                   interval='1h',                   start=start,                    #end=,                   auto_adjust = True,                   progress=True)#%% Import Tweets#%% EDA and Prep - Pricesprices = data.Closeprices.plot()log_returns = np.log(prices).diff()log_returns.plot()def assign_class(ret, thereshold=0.01):    if ret > thereshold:        return 1    elif ret < -thereshold:        return -1    else:        return 0        classes = log_returns.apply(assign_class)#%% EDA and prep - Tweetsif __name__ == "__main__":   print("im a bg")